import { normalize } from './normal';
/**
 * @interface cookie 设置属性
 */
export interface CookieOptions {
    /**
     * domain 域名，默认为当前域名，如果域名配置存在跨域问题则创设置失败
     */
    domain?: string;
    /**
     * path 域名路径，默认为 /，如果路径配置不合法则设置失败，例如设置成子路径
     */
    path?: string;
    /**
     * expires 接受number和Date类型，number以秒为单位
     */
    expires?: number | Date;
    /**
     * secure 必须在https下设置，http下设置失败。 true：https下才能传递和获取cookie false：https/http 均可传递和获取cookie
     */
    secure?: boolean;
    /**
     * sameSite 阻止浏览器将 cookie 与跨站点请求一起发送。设置为none需配合secure:true，存在跨站点请求，默认设置为none
     */
    sameSite?: 'strict' | 'lax' | 'none';
}
/**
 * @ignore
 */
export { normalize };
