import { CookieOptions } from './normalize/index';
/**
 * 获取cookie
 *
 * 1、可获取当前域名以及父域名下当前路径及父路径cookie。
 * ```
 * const value = cookie.get('a')
 * ```
 * @param name cookie name
 * @returns cookie value 如果匹配不到指定cookie name，则返回undefined
 */
export declare function get(name: string): string | void;
/**
 * 设置cookie
 *
 * 1、可设置当前域名及父域名下任何路径cookie。
 * ```
 * // 默认path为'/'
 * cookie.set('b','e')
 * // 设置session cookie，但不建议设置session cookie，部分浏览器退出以后并不会清除
 * cookie.set('b', 'value')
 * ```
 * @param name cookie name
 * @param value cookie value
 * @param opts cookie attributes
 */
export declare function set(name: string, value: string, opts?: CookieOptions): void;
/**
 * 删除cookie
 *
 * 1、删除规则与set保持一致
 * ```
 * // 删除当前域名cookie
 * cookie.remove('a')
 * ```
 * @param name cookie name
 * @param opts cookie attributes
 */
export declare function remove(name: string, opts?: CookieOptions): void;
