/**
 * DateHelper 类
 */
declare class DateHelper {
    /**
     * 格式化时间
     * @remarks
     * 格式化符号有下列几种
     * yyyy : 四位数的年份。例如：2018
     * yy   : 两位数的年份。例如：18
     * MM   : 两位数月份，从 01 开始。01-12
     * M    : 一位数月份，从 1 开始。1-12
     * dd   : 两位天数。01-31
     * d    : 一位天数。1-31
     * HH   : 两位小时，24小时制。00-23
     * H    : 一位小时，24小时制。0-23
     * mm   : 两位分钟。00-59
     * m    : 一位分钟。0-59
     * ss   : 两位秒。00-59
     * s    : 一位秒。0-59
     * E    ：星期数。0-6，星期天是 0
     * @param date - 日期对象
     * @param format
     * @returns 格式化的时间字符串
     */
    format(date: Date, format: string): string;
    /**
     * 解析时间
     * @remarks
     * 格式化符号有下列几种
     * yyyy : 四位数的年份。例如：2018
     * yy   : 两位数的年份。例如：18
     * MM   : 两位数月份，从 01 开始。01-12
     * M    : 一位数月份，从 1 开始。1-12
     * dd   : 两位天数。01-31
     * d    : 一位天数。1-31
     * HH   : 两位小时，24小时制。00-23
     * H    : 一位小时，24小时制。0-23
     * mm   : 两位分钟。00-59
     * m    : 一位分钟。0-59
     * ss   : 两位秒。00-59
     * s    : 一位秒。0-59
     * @param input - 格式化的时间字符串
     * @param format
     * @returns 日期对象
     */
    parse(input: string, format: string): Date;
}
export default DateHelper;
