interface result{
    status: { [key:string]: any };
    result: { [key:string]: any } | boolean | {};
}

interface isOptions{
    environment?: number;

    success?(result: result): void;

    error?(result: result): void;
}

interface qqLoginOptions{
    url?: string;

    environment?: number;

    device: number;
}

interface urlOption{
    url?: string;

    environment?: number;
}

interface wechatForceLoginOption{
    environment: number;
}

interface loginOptions{
    url?: string;

    environment?: number;

    oauth?: number;

    device?: number;

    force?: number;

    bind?: number;

    seller?: number;
}

export function isLogin() : boolean

export function isSellerLogin() : boolean

export function isWechatLogin() : boolean

export function isQQLogin() : boolean

export function isSubAccount() : boolean

export function isWeixin() : boolean

export function isQQ() : boolean

export function getUserInfo(options: isOptions) : void

export function isOpenShop(options: isOptions) : void

export function qqLogin(options: qqLoginOptions) : string

export function wechatSlientLogin(options: urlOption) : string

export function wechatForceLogin(options?: wechatForceLoginOption) : string

export function getBindStatus(options: isOptions) : void

export function login(options: loginOptions) : string

export function logout(options: urlOption) : string

export function bind(options: urlOption) : string
