import interceptRequest from './ajax';
import interceptJsonp from './jsonp';
import interceptFetch from './fetch';
import { isObject, isFunction } from '../../../util';
/**
 * @ignore
 */
export default function intercept(options) {
    if (!isObject(options) || !isFunction(options.beforeSuccess)) {
        return;
    }
    interceptRequest(options);
    interceptJsonp(options);
    interceptFetch(options);
}
