import { isString } from '@vdian/util';
export * from './proxy';
var RPX_REGEXP = /(\.\d+|\d+(\.\d+)?)\s*rpx\b/gi;
/**
 *
 *  替换
 * @ignore
 */
export function replaceRpx(value, calculate) {
    if (isString(value)) {
        value = value.replace(RPX_REGEXP, function (match) {
            // 找出rpx 数字值
            var str = match.substr(0, match.length - 3);
            // string to number
            var number = Number(str);
            return !isNaN(number) ? calculate(number) : match;
        });
    }
    return value;
}
