// 触发mutationCallback时，节点都已添加，请求已发送，所以只能阻止显示，故可合并多次节点改变延时执行
var delayedHandler = debounce(function () {
    var nodes = DOCUMENT.querySelectorAll(DELAYED_NODE_NAMES);
    nodes.forEach(function (node) {
        DELAYED_HANDLERS[node.nodeName](node);
    });
});
function mutationCallback(records) {
    immediatelyHandler(records);
    delayedHandler();
}
var observer;
/**
 * @ignore
 */
export function observe() {
    if (MutationObserver && !observer) {
        observer = new MutationObserver(mutationCallback);
        observer.observe(DOCUMENT, {
            childList: true,
            attributes: true,
            subtree: true,
            attributeFilter: ['href', 'src']
        });
    }
}
/**
 * @ignore
 */
export function disconnect() {
    if (observer) {
        // 由于delayedHandler存在延迟，disconnect后可能会移除不合法节点
        observer.disconnect();
        observer = undefined;
    }
}
