export { escape } from './escape';
export { jump } from './jump';
export { isLegalUrl, legalizeUrl, addWhiteDomain } from './url-legalize';
/**
 * @interface 配置属性
 */
export interface SafetyOptions {
    /**
     * 是否监听DOM节点，默认为不开启
     */
    observeDom?: boolean;
    /**
     * 是否监听在Iframe中，默认为开启
     */
    observeIframe?: boolean;
    /**
     * 解析URL函数，用于自定义链接处理规则
     */
    resolveUrl?: (url: string) => string;
}
/**
 * 用于配置安全库相关属性
 * @param options 配置属性
 */
export declare function option(options: SafetyOptions): void;
