import { observe, disconnect } from './dom-observe';
import { access } from './util';
export { escape } from './escape';
export { jump } from './jump';
export { isLegalUrl, legalizeUrl, addWhiteDomain } from './url-legalize';
// 设置默认值
access({
    observeIframe: true
});
/**
 * 用于配置安全库相关属性
 * @param options 配置属性
 */
export function option(options) {
    access(options);
    // observe or disconnect
    access("observeDom" /* OBSERVE_DOM */) ? observe() : disconnect();
}
