import { Params } from '../querystring';
/**
 * Url类
 * ```
 * const url = new Url('https://www.weidian.com/')
 * // get url href
 * let href = url.href
 * // set url href
 * url.href = 'https://www.koudai.com/a.html'
 * console.log(url.host) // www.koudai.com
 * ```
 */
export declare class Url {
    /**
     * query 为参数对象，可修改或者追加参数，为了更好的兼容与容错，与URLSearchParams稍有不同，参数value序列化规则如下：
     *  1. 对于 null/undefined，参数设置无效。
     *  2. 对于空字符串，参数设置为空字符串。
     *  3. 对于数组，参数设置为name=array[0]&name=array[1]……。
     *  4. 其他类型均调用 encodeURIComponent 进行序列化。
     */
    query: Params;
    search: string;
    hash: string;
    host: string;
    hostname: string;
    pathname: string;
    port: string;
    protocol: string;
    href: string;
    /**
     * origin 为只读属性，不可修改。
     */
    readonly origin: string;
    private _element;
    /**
     * Url
     * @param url 链接地址，未传递使用当前页面链接，如果为相对地址，则会以当前页面url作为base url。
     */
    constructor(url?: string);
    /**
     * toString方法，在String(instance)调用。
     * @returns href属性。
     */
    toString(): string;
}
